mapper.update:
UPDATE product
category_id = #{categoryId}, name = #{name}, subtitle = #{subtitle}, main_image = #{mainImage}, images = #{images}, detail = #{detail}, price = #{price}, stock = #{stock}, status = #{status}, updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}

UPDATE product
SET stock = stock - #{quantity},
    updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND stock >= #{quantity}
